import MenuView from '@/components/MenuView';
import { Box, Button, Stack, Tab } from '@mui/material';
import TabPanel from '@mui/lab/TabPanel';
import TabContext from '@mui/lab/TabContext';
import TabList from '@mui/lab/TabList';
import TextField from '@mui/material/TextField';

import React, { useCallback, useState } from 'react';
import { styled } from '@mui/material/styles';

const VisuallyHiddenInput = styled('input')({
  clip: 'rect(0 0 0 0)',
  clipPath: 'inset(50%)',
  height: 1,
  overflow: 'hidden',
  position: 'absolute',
  bottom: 0,
  left: 0,
  whiteSpace: 'nowrap',
  width: 1,
});

const UploadImg = styled('img')({
  maxHeight: '100%',
  maxWidth: '100%',
});

const OutImg = styled('img')({
  Width: '100%',
  border: 'black solid 1px',
  borderColor: 'rgba(0, 0, 0, 0.1)',
  margin: '20px',
  padding: '20px',
  borderRadius: '3px',
});

const MySpan = styled('span')({});

const ImgBase64: React.FC = () => {
  const [decodeIn, setdecodeIn] = useState<string>(
    'data:image/svg+xml;base64,'
  );
  const [encodeIn, setencodeIn] = useState<string>('');
  const [encodeOut, setencodeOut] = useState<string>('');
  const [decodeOut, setdecodeOut] = useState<string>('');

  const onDecodeInChange = useCallback(
    (event: React.ChangeEvent<HTMLInputElement>) => {
      setdecodeIn(event.target.value);
    },
    []
  );

  const handleSelectFile = useCallback(
    (event: React.ChangeEvent<HTMLInputElement>) => {
      const files = event.target.files || [];
      if (files.length == 0) {
        return;
      }
      const reader = new FileReader();
      reader.onload = function (e) {
        if (e.target !== null) {
          setencodeIn(e.target.result as string);
        }
      };
      reader.readAsDataURL(files[0]);
    },
    []
  );

  const decode = useCallback(() => {
    setdecodeOut(decodeIn);
  }, [decodeIn]);

  const encode = useCallback(() => {
    setencodeOut(encodeIn);
  }, [encodeIn]);

  const [tabValue, setTabValue] = React.useState('1');

  const handleTabChange = (event: React.SyntheticEvent, newValue: string) => {
    setTabValue(newValue);
  };

  return (
    <MenuView>
      <Stack
        sx={{
          mt: '24px',
          gap: '18px',
          maxWidth: '1020px',
          fontFamily: 'Mono',
          
          mx: 'auto',
        }}
      >
        <Box sx={{ width: '100%', typography: 'body1' }}>
          <TabContext value={tabValue}>
            <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
              <TabList onChange={handleTabChange}>
                <Tab
                  label='图片转 Base64'
                  value='1'
                  sx={{ textTransform: 'none !important' }}
                />
                <Tab
                  label='Base64 转图片'
                  value='2'
                  sx={{ textTransform: 'none !important' }}
                />
              </TabList>
            </Box>
            <TabPanel value='1' sx={{ paddingLeft: 0, paddingRight: 0 }}>
              <Stack spacing={1} sx={{ color: '#FF1844' }}>
                <Button
                  component='label'
                  variant='outlined'
                  sx={{ borderRadius: '3px', height: '179px' }}
                >
                  {encodeIn ? <MySpan></MySpan> : <MySpan>选择图片</MySpan>}
                  <UploadImg src={encodeIn} />
                  <VisuallyHiddenInput
                    type='file'
                    onChange={handleSelectFile}
                  />
                </Button>
                <Button
                  size='small'
                  sx={{
                    borderRadius: '4px',
                  }}
                  component='label'
                  variant='contained'
                  color='primary'
                  onClick={encode}
                >
                  转换
                </Button>
                <TextField
                  value={encodeOut}
                  variant='outlined'
                  multiline
                  rows={6}
                  InputProps={{ readOnly: true }}
                  sx={{ textarea: { fontSize: '14px', fontFamily: 'Mono' } }}
                />
              </Stack>
            </TabPanel>
            <TabPanel value='2' sx={{ paddingLeft: 0, paddingRight: 0 }}>
              <Stack spacing={1} sx={{ color: '#FF1844' }}>
                <TextField
                  value={decodeIn}
                  variant='outlined'
                  multiline
                  rows={6}
                  onChange={onDecodeInChange}
                  sx={{ textarea: { fontSize: '14px', fontFamily: 'Mono' } }}
                />
                <Button
                  size='small'
                  sx={{
                    borderRadius: '4px',
                  }}
                  component='label'
                  variant='contained'
                  color='primary'
                  onClick={decode}
                >
                  转换
                </Button>
                <OutImg src={decodeOut} />
              </Stack>
            </TabPanel>
          </TabContext>
        </Box>
      </Stack>
    </MenuView>
  );
};

export default ImgBase64;
